			cFunc["createEnterZombies"]()				cSetting["entered"] = true			end		end	endend)addEventHandler("onClientMarkerHit", cSetting["outmarker"], function(h)	if(h == localPlayer) then		callServerFunction("setInPosition", localPlayer, -50.628833770752, -231.12879943848, 6.7646160125732, 0)	endend)